+// Return true if aFrame1 is after aFrame2
+static PRBool IsFrameAfter(nsIFrame* aFrame1, nsIFrame* aFrame2)
+{
+  nsIFrame* f = aFrame2;
+  do {
+    f = f->GetNextSibling();
+    if (f == aFrame1)
+      return PR_TRUE;
+  } while (f);
+  return PR_FALSE;
