+
+function getLinkFile()
+{
+  if (isWindows) {
+    return do_get_file(""netwerk/test/unit/test_link.url"");
+  }
+  if (isLinux) {
+    return do_get_file(""netwerk/test/unit/test_link.desktop"");
+  }
